import React from 'react';
import './../BaseUI.css'
import { NavLink } from 'react-router-dom';
import './Nav.css'
import { useState } from 'react';
function Nav() {
    const [navstyle,setNavStyle]=useState("")
    const NavlinkStyles=({ isActive})=>{
        if(navstyle==="")
         this.setState({navstyle:{backgroundColor:"gray"}})
         else{
             this.setState({navstyle:{backgroundColor:""}})
         }
         return {fonWeight:isActive?'bold':'normal'}
     }
    return ( 
        <>
         <div className="nav" style={this.state.navstyle}>
 <img src="logo.png" alt="" style={{}}/>
 <div className="row">
 <button className='btn' ><NavLink to='/'>Home</NavLink></button>
 <button className='btn'><NavLink style={this.NavlinkStyles} to='/Products'>Our Products</NavLink></button>
 <button className='btn'><NavLink style={this.NavlinkStyles} to='/Contact'>Contact Us</NavLink></button>
 </div>
 </div>
        </>
     );
}

export default Nav;
